{
    "session": "{{session}}",
    "id": 1,
    "method": "set",
    "params": [
        {
            "url": "/dvmdb/adom/{{adom}}/script",
            "data": [
                {
                    "name": "PIP_&_Broadband_Spoke_CSF",
                    "type": "cli",
                    "desc": "CSF Configurations for PIP + Broadband Spoke",
                    "content": "# BGP Configurations

config system csf
    set status enable
    set upstream-ip 10.10.1.250
    set management-ip {{WAN_1_IP}}
end
config system saml
    set default-profile \"super_admin\"
end

",
                    "target": "remote_device"
                }
            ]
        }
    ]
}

